export const initState = {
  todo: ["my first Todo"]
};

export const todoReducer = (state, action) => {
  switch (action.type) {
    case "ADD_TODO":
      return {
        todo: [...state.todo, action.payload]
      };
    case "REMOVE_TODO":
      return {
        todo: action.payload
      };
    default:
      return state;
  }
};
