export const initState = {
  todo: ["my first Todo"]
};

export const todoReducer = (state, action) => {
  switch (action.type) {
    case "ADD_TODO":
      return {
        ...state,
        todo: [...state.todo, action.payload]
      };
    case "REMOVE_TODO":
      return {
        ...state,
        todo: action.payload
      };
    default:
      return state;
  }
};
