export const initState = ["my first Todo"];

export const todoReducer = (state, action) => {
  switch (action.type) {
    case "ADD_TODO":
      console.log([...state, action.payload]);
      return [...state, action.payload];

    case "REMOVE_TODO":
      return {
        ...state,
        todo: action.payload
      };
    default:
      return state;
  }
};
